INSERT INTO cms.t_article (id, article_title, sub_title, type_id, type_name, simple_intro, content, title_color, bold_flag, review_flag, top_flag, ad_flag, delete_flag, recommend_flag, keyword, status, delete_no, delete_name, delete_time, add_no, add_name, add_time, update_no, update_name, update_time) VALUES (9, '疫情下的典当行:有顾客为借6万 当了价值近百万宝石', '（原标题：探访疫情下的典当行：有顾客当价值近百万元的宝石借了6万元）', 2, null, '', '<p class="otitle" style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">&nbsp;</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">&nbsp;</p>
<div class="video-wrapper" style="margin: 10px auto; color: #404040; font-family: ''Microsoft Yahei''; font-size: 18px; text-align: justify; background: #f5f5f5;">
<div class="video-inner" style="padding: 25px 25px 20px;">
<div class="video" style="background: #000000; max-height: 450px;"><video style="height: 450px; max-height: 450px;" poster="https://videoimg.nos-jd.163yun.com/cmssnapshot/20200531/vdPi12793_2.jpg" preload="auto" controls="controls" width="100%"></video></div>
<div class="video-info clearfix" style="margin-top: 16px; height: 20px; line-height: 20px;">
<p class="fn-right" style="font-size: 12px; height: 22px; line-height: 22px; overflow: hidden; float: right;"><a class="video-title" style="color: #000000; text-align: right; float: left; width: 300px;" href="https://v.163.com/static/3/VKD1NEIQ2.html" target="_blank" rel="noopener">探访疫情下的典当行：有顾客当价值近百万元的宝石借了6万元</a><span class="video-from" style="color: #a7a7a7; margin-left: 10px;">（来源：梨视频）</span></p>
</div>
<div class="video-list">&nbsp;</div>
</div>
</div>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">&nbsp;</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">&ldquo;我们没停业，有小微企业和客户急需用钱&rdquo;。疫情下典当行生意如何？拍客探访发现，典当行几千元的小额借贷增多，当品主要是首饰玉器车子等，有顾客当了价值近百万的宝石借了6万元，当期到后沟通多次仍没赎回。</p>', null, null, 1, null, null, 0, null, null, 3, null, null, null, '1', 'admin', '2020-05-31 09:51:53', null, null, null);
INSERT INTO cms.t_article (id, article_title, sub_title, type_id, type_name, simple_intro, content, title_color, bold_flag, review_flag, top_flag, ad_flag, delete_flag, recommend_flag, keyword, status, delete_no, delete_name, delete_time, add_no, add_name, add_time, update_no, update_name, update_time) VALUES (10, '央视:"学生被罚致吐血"反转 公众正义感不该被亵渎', '（原标题：热评丨“老师体罚学生致吐血”系谎言：公众正义感不该被亵渎）', 2, null, '', '<p class="otitle" style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">）</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">&nbsp;</p>
<div class="video-wrapper" style="margin: 10px auto; color: #404040; font-family: ''Microsoft Yahei''; font-size: 18px; text-align: justify; background: #f5f5f5;">
<div class="video-inner" style="padding: 25px 25px 20px;">
<div class="video" style="background: #000000; max-height: 450px;"><video style="height: 343.125px; max-height: 450px;" poster="https://videoimg.ws.126.net/cover/20200530/p3qmAj9sa_cover.jpg" preload="auto" controls="controls" width="100%"></video></div>
<div class="video-info clearfix" style="margin-top: 16px; height: 20px; line-height: 20px;">
<p class="fn-right" style="font-size: 12px; height: 22px; line-height: 22px; overflow: hidden; float: right;"><a class="video-title" style="color: #000000; text-align: right; float: left; width: 300px;" href="https://v.163.com/paike/VDHIRLLDS/VKCV1OEEJ.html" target="_blank" rel="noopener">官方回应&ldquo;班主任罚跑6岁哮喘女童&rdquo;：事实有出入</a><span class="video-from" style="color: #a7a7a7; margin-left: 10px;">（来源：original）</span></p>
</div>
<div class="video-list">&nbsp;</div>
</div>
</div>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">&nbsp;</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">5月30日，有用户发帖，声称&ldquo;广州市方圆实验小学教师体罚学生致学生吐血&rdquo;，相关帖子被大量转发、评论，迅速成为网络热点。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">&nbsp;</p>
<p class="f_center" style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; color: #404040; text-align: center; background-color: #ffffff;"><img style="vertical-align: top; border: 0px; max-width: 550px;" src="https://cms-bucket.ws.126.net/2020/0531/58d212aaj00qb6rpk002xc000lc00ljc.jpg" alt="央视:学生被罚致吐血反转 公众正义感不该被亵渎" /></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">然而，事件很快反转。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">5月31日凌晨，广州白云警方发布通报称，经调查取证，发帖人刘某承认，&ldquo;其女儿因遭体罚吐血&rdquo;&ldquo;凌晨2时被老师威胁殴打&rdquo;&ldquo;送老师6万元&rdquo;等情节，系其为扩大事件影响而故意编造的谎言，照片展示的衣服&ldquo;血迹&rdquo;实为化妆品和水，其女儿目前精神状态良好。据接诊刘某女儿的医院反映，就诊过程中患者及其家属均未提及哮喘病史和吐血的情况，刘某目前亦无法提供其女儿哮喘诊断的有关病历证明。与此同时，警方在调查中还发现了刘某涉嫌雇请人员进行网络炒作的相关证据。</p>
<p class="f_center" style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; color: #404040; text-align: center; background-color: #ffffff;"><img style="vertical-align: top; border: 0px; max-width: 550px;" src="https://cms-bucket.ws.126.net/2020/0531/47e34a69j00qb6rpk001sc000uu00hac.jpg" alt="央视:学生被罚致吐血反转 公众正义感不该被亵渎" /></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">另据白云教育局5月31日通报，经调查，2019年12月10日，刘老师因学生违反纪律，以班规为由让刘某女儿等5名违纪学生跑10圈。校方已于去年12月暂停了刘老师班主任职务，并对其进行全校通报批评、免去其品德学科科组长职务。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">家长刘某如果认为这样的处罚过轻，也应走正常途径维权，而不能通过不正当甚至违法的方式实现目的。从目前结果看，该家长通过编造谎言的确实现了&ldquo;扩大影响&rdquo;的目的，但谎言终究是谎言，编造谎言者也须为自己行为付出代价。</p>
<p class="f_center" style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; color: #404040; text-align: center; background-color: #ffffff;"><img style="vertical-align: top; border: 0px; max-width: 550px;" src="https://cms-bucket.ws.126.net/2020/0531/ba38e28dj00qb6rpk002fc000hs00c7c.jpg" alt="央视:学生被罚致吐血反转 公众正义感不该被亵渎" /></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">涉事学校</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">在家长刘某的编造中，刘老师&ldquo;体罚学生致吐血&rdquo;&ldquo;凌晨袭击学生家长&rdquo;&ldquo;收受家长钱财&rdquo;&hellip;&hellip;显得面目可憎，其社会评价因谣言而显著降低，网友也因此对其恶言相向。目前施行的民法总则等法律，以及刚刚获得通过并将于明年1月1日起实施的民法典，都对公民名誉权作出规定，禁止任何组织或者个人以侮辱、诽谤等方式侵害他人名誉权。在这一事件中，如果刘老师认为自己名誉权因谎言受损，有权要求对方停止侵害，恢复名誉，消除影响，赔礼道歉，并作出精神损害赔偿。</p>
<p class="f_center" style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; color: #404040; text-align: center; background-color: #ffffff;"><img style="vertical-align: top; border: 0px; max-width: 550px;" src="https://cms-bucket.ws.126.net/2020/0531/5d8cf974j00qb6rpl000tc000s800inc.jpg" alt="央视:学生被罚致吐血反转 公众正义感不该被亵渎" /></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">家长刘某应承担的责任，并不止于民事层面。刑法规定，诽谤罪指的是捏造事实诽谤他人，情节严重的行为。而根据《最高人民法院、最高人民检察院关于办理利用信息网络实施诽谤等刑事案件适用法律若干问题的解释》&mdash;&mdash;&ldquo;捏造损害他人名誉的事实，在信息网络上散布，或者组织、指使人员在信息网络上散布的&rdquo;，应当认定为&ldquo;捏造事实诽谤他人&rdquo;；&ldquo;同一诽谤信息实际被点击、浏览次数达到五千次以上，或者被转发次数达到五百次以上的&rdquo;，应当认定为&ldquo;情节严重&rdquo;。考虑到该事件中谎言的诽谤性质及传播效果，该家长或面临刑事追究。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">需要说明的是，除了严重危害社会秩序和国家利益的行为，诽谤罪属于自诉案件，由被害人直接向法院提起刑事自诉。本案是否属于司法解释规定的严重危害社会秩序和国家利益情形，需要办案机关判断，如果办案机关认为不属于，被中伤的老师可以向法院起诉，要求追究诽谤者刑事责任。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">另一方面，从公众角度来看，不少人出于同情心、正义感而转发、评论了该家长发布的相关内容，结果发现核心信息全是假的，他们一定有被愚弄的感觉。近年来，在一些公共事件中，公众的关注对事件最终解决曾起到不小的推动作用，网络平台也在实现社会正义方面发挥了重要作用。然而，仍有人误认为网络是法外之地，试图利用谎言误导公众，以实现个人不正当目的。他们的行为既侵犯了他人合法权益，也亵渎了公众的正义感，还有让网络失去公信力的危险。一旦网络公信力缺失，网民因拿不准信息真假而失去&ldquo;围观&rdquo;、转发的动力和兴趣，网络本有的积极力量将在一定程度上被消解。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">网络本是社会公器，容不得个别人为了实现不正当目的而私用。希望这一事件能让有类似企图者警醒。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">&nbsp;</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">&nbsp;</p>
<div class="gg200x300" style="padding: 19px; margin-right: 40px; overflow: hidden; text-align: center; font-size: 0px; line-height: 0; float: left; border: 1px solid #e5e5e5; position: relative; color: #404040; font-family: ''Microsoft Yahei''; background-color: #ffffff;">
