INSERT INTO cms.t_article (id, article_title, sub_title, type_id, type_name, simple_intro, content, title_color, bold_flag, review_flag, top_flag, ad_flag, delete_flag, recommend_flag, keyword, status, delete_no, delete_name, delete_time, add_no, add_name, add_time, update_no, update_name, update_time) VALUES (22, '家长注意：宝宝眼睛有这8种症状 赶紧去医院', '', 5, null, '', '<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">眼睛是心灵的窗户，尤其是对宝宝来说，眼睛的发育关系着宝宝将来视力的情况，重要性自然不言而喻。可有些家长却发现，宝宝都满月了，怎么眼睛还不追物呢？会不会是视力有问题呀？今天我们就来探讨一下这个问题。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;"><span style="font-weight: bold;">宝宝的视力是怎么发育的？</span></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">海淀区妇幼保健院眼保健中心主任、副主任医师陈巍介绍，刚出生的宝宝是看不清东西的，随着慢慢地发育，直到6岁时宝宝的视力才能达到1.0。视觉发育跟身高一样，每个孩子的视觉发育都有一个年龄段，一般在0-6岁之间。因此，孩子6岁前的视觉发育水平，基本决定了一生的视觉质量。如果在这个年龄段视力没有正常发育，长大后将难以弥补。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">通常满月的宝宝看到自己感兴趣的事物时，眼睛会随之转动，但有家长发现，自己的宝宝虽然满月了，但从没&ldquo;正眼&rdquo;瞧过自己，眼睛从来不瞅人，也不会追物，难不成是视力有问题？</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">其实，每个宝宝的发育情况并不一样，有的宝宝发育比较早，很早眼睛就会追物了，有的宝宝发育比较晚，6-8周时才会追物，但这并不影响宝宝的正常视觉发育，家长可不必担心。平时可多训练宝宝追视，比如可利用颜色鲜艳的玩具吸引宝宝注意力，或用可移动的玩具训练宝宝追物等。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;"><span style="font-weight: bold;">宝宝视力筛查出生后就应开始</span></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">要想宝宝的视力健康发展，家长们可别等到发现问题再去看，那可就晚啦！专家的意思是，宝宝视力筛查应该从&ldquo;0&rdquo;开始，一些<a style="color: #0f6b99; text-decoration-line: underline;" title="新生儿" href="https://baby.163.com/special/00262MI3/newbaby08.html">新生儿</a>出生后便患有先天性白内障、眼部恶性肿瘤等眼部疾病，而且6岁之前的许多眼病，都可能影响视力发育。所以从宝宝一出生就应该做眼睛的健康筛查，宝宝眼病要早发现、早治疗。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">另外，由于宝宝在3岁前还不会表达，家长便很难及时发现宝宝存在的各种视力问题。所以家长们要掌握方法，注意观察，以防错过孩子视觉异常的治疗黄金期，造成不可逆的眼部疾病。</p>', null, null, 1, null, null, 0, null, null, 3, null, null, null, '1', 'admin', '2020-05-31 09:59:57', null, null, null);
INSERT INTO cms.t_article (id, article_title, sub_title, type_id, type_name, simple_intro, content, title_color, bold_flag, review_flag, top_flag, ad_flag, delete_flag, recommend_flag, keyword, status, delete_no, delete_name, delete_time, add_no, add_name, add_time, update_no, update_name, update_time) VALUES (23, '体罚哮喘学生致吐血?官方:涉事教师暂停班主任职务', '（原标题：广州白云教育局通报教师涉嫌体罚学生）', 5, null, '', '<p class="otitle" style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">&nbsp;</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">&nbsp;</p>
<div class="video-wrapper" style="margin: 10px auto; color: #404040; font-family: ''Microsoft Yahei''; font-size: 18px; text-align: justify; background: #f5f5f5;">
<div class="video-inner" style="padding: 25px 25px 20px;">
<div class="video" style="background: #000000; max-height: 450px;"><video style="height: 343.125px; max-height: 450px;" poster="https://videoimg.ws.126.net/cover/20200531/taJ3qx9s2_cover.jpg" preload="auto" controls="controls" width="100%"></video></div>
<div class="video-info clearfix" style="margin-top: 16px; height: 20px; line-height: 20px;">
<p class="fn-right" style="font-size: 12px; height: 22px; line-height: 22px; overflow: hidden; float: right;"><a class="video-title" style="color: #000000; text-align: right; float: left; width: 300px;" href="https://v.163.com/paike/VC40MP14M/VHD1FQMQR.html" target="_blank" rel="noopener">教师涉嫌体罚学生已被停职，教育局调监控还原事发经过</a><span class="video-from" style="color: #a7a7a7; margin-left: 10px;">（来源：~）</span></p>
</div>
<div class="video-list">&nbsp;</div>
</div>
</div>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">&nbsp;</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">广州白云教育5月31日通报 ： 关于网传广州市方圆实验小学一年级5班教师涉嫌体罚学生致其吐血一事，现将有关情况作如下通报：</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">&nbsp;</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">据广州白云公安5月31日凌晨的通报，发帖人刘某（微博名：小岛里的大海）承认其女儿因遭体罚吐血、凌晨2时被老师威胁殴打、送老师6万元等情节，系其为扩大影响而故意编造的谎言，照片展示的衣服&ldquo;血迹&rdquo;实为化妆品和水，其女儿目前精神状态良好。据接诊医院反映，就诊过程中患者及其家属均未提及哮喘病史和吐血的情况，目前亦无法提供其女儿哮喘诊断的有关病历证明。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">经区教育局调查，2019年12月10日，刘妍老师因学生违反学生管理纪律，以班规为由让刘某女儿等5名违纪学生跑10圈。视频监控显示，刘某女儿于当天14:55开始慢跑第1圈，跑跑走走停停共完成9圈，15:27离开操场，15:35至16:30上思政课和自习课时无异常状况，16:30放学后在校门由刘某接走，监控视频显示刘某女儿身体和精神无异常状况。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">学校已于2019年12月12日暂停刘妍老师班主任职务，并对其进行全校通报批评、免去其品德学科科组长职务等处理。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">区教育局将继续强化学校监督管理，督促学校加强教师队伍建设，不断改进教育教学方法。</p>', null, null, 1, null, null, 0, null, null, 3, null, null, null, '1', 'admin', '2020-05-31 10:00:21', null, null, null);
INSERT INTO cms.t_article (id, article_title, sub_title, type_id, type_name, simple_intro, content, title_color, bold_flag, review_flag, top_flag, ad_flag, delete_flag, recommend_flag, keyword, status, delete_no, delete_name, delete_time, add_no, add_name, add_time, update_no, update_name, update_time) VALUES (24, '抗议持续升级！美国警方在17个城市逮捕近1400人', '原标题：抗议持续升级！美媒：警方在全国17个城市逮捕近1400人）', 6, null, '', '<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">环球网报道】美国明尼苏达州白人警察涉嫌暴力执法致死黑人男子乔治&middot;弗洛伊德引发的抗议示威持续升级，目前全美至少30座城市爆发抗议活动。据美联社最新消息，美国17个城市的示威活动中，已有将近1400人被警方逮捕。</p>
<p class="f_center" style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; color: #404040; text-align: center; background-color: #ffffff;"><img style="vertical-align: top; border: 0px; max-width: 550px; margin-right: auto; margin-left: auto;" src="https://cms-bucket.ws.126.net/2020/0531/fd5c93cep00qb6gdl0006c000r00081c.png" data-alt="美联社报道截图" data-upload-link="%7B%22cover%22%3A%22%22%2C%22desc%22%3A%221.png%22%2C%22id%22%3A%228a8a562bd2996a0ca9289695818ac982u1%22%2C%22size%22%3A6.48%2C%22width%22%3A972%2C%22height%22%3A289%2C%22url%22%3A%22https%3A%2F%2Frs1.huanqiucdn.cn/dp/api/images%2FimageDir%2F8a8a562bd2996a0ca9289695818ac982u1.png%22%2C%22tags%22%3A%5B%5D%2C%22time%22%3A%222020-05-31%2011%3A50%3A11%22%2C%22mime%22%3A%22image%2Fpng%22%7D" /></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">美联社报道截图</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">报道称，据美联社统计，自周四以来，至少有1383人被捕。此外，由于周六晚上的示威活动仍在持续，实际逮捕人数可能会更多。</p>
<p class="f_center" style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; color: #404040; text-align: center; background-color: #ffffff;"><img style="vertical-align: top; border: 0px; max-width: 550px; margin-right: auto; margin-left: auto;" src="https://cms-bucket.ws.126.net/2020/0531/1bdd566ej00qb6gdl001bc000m800etc.jpg" data-alt="5月30日，一名示威者在纽约声援弗洛伊德的集会活动中被警方逮捕。图源：美联社" data-upload-link="%7B%22cover%22%3A%22%22%2C%22desc%22%3A%222.jpg%22%2C%22id%22%3A%224a3812b2543bc93fe9bede5788270f5eu5%22%2C%22size%22%3A47.8%2C%22width%22%3A800%2C%22height%22%3A533%2C%22url%22%3A%22https%3A%2F%2Frs1.huanqiucdn.cn/dp/api/images%2FimageDir%2F4a3812b2543bc93fe9bede5788270f5eu5.jpg%22%2C%22tags%22%3A%5B%5D%2C%22time%22%3A%222020-05-31%2011%3A50%3A25%22%2C%22mime%22%3A%22image%2Fjpeg%22%7D" /></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">&nbsp;</p>
<div class="gg200x300" style="padding: 19px; margin-right: 40px; overflow: hidden; text-align: center; font-size: 0px; line-height: 0; float: left; border: 1px solid #e5e5e5; position: relative; color: #404040; font-family: ''Microsoft Yahei''; background-color: #ffffff;">
<div id="ssp_6056789"><iframe id="iframeu6056789_0" style="display: block; border-width: 0px; border-style: initial; vertical-align: bottom; width: 300px; height: 250px;" src="https://pos.baidu.com/ncfm?conwid=300&amp;conhei=250&amp;rdid=6056789&amp;dc=3&amp;exps=110011&amp;psi=69e8182df13e54fd918e666a885d0dcf&amp;di=u6056789&amp;dri=0&amp;dis=0&amp;dai=2&amp;ps=1390x551&amp;enu=encoding&amp;ant=0&amp;dcb=___adblockplus_&amp;dtm=HTML_POST&amp;dvi=0.0&amp;dci=-1&amp;dpt=none&amp;tsr=0&amp;tpr=1590919237483&amp;ti=%E6%8A%97%E8%AE%AE%E6%8C%81%E7%BB%AD%E5%8D%87%E7%BA%A7%EF%BC%81%E7%BE%8E%E5%9B%BD%E8%AD%A6%E6%96%B9%E5%9C%A817%E4%B8%AA%E5%9F%8E%E5%B8%82%E9%80%AE%E6%8D%95%E8%BF%911400%E4%BA%BA_%E7%BD%91%E6%98%93%E6%96%B0%E9%97%BB&amp;ari=2&amp;ver=0527&amp;dbv=2&amp;drs=1&amp;pcs=1903x979&amp;pss=1903x3161&amp;cfv=0&amp;cpl=3&amp;chi=1&amp;cce=true&amp;cec=GBK&amp;tlm=1590919237&amp;prot=2&amp;rw=979&amp;ltu=https%3A%2F%2Fnews.163.com%2F20%2F0531%2F11%2FFDV2HC9800019B3E.html&amp;ltr=https%3A%2F%2Fnews.163.com%2F&amp;lcr=https%3A%2F%2Fwww.google.com.hk%2F&amp;ecd=1&amp;uc=1920x1050&amp;pis=-1x-1&amp;sr=1920x1080&amp;tcn=1590919237&amp;qn=b520aa738663218a&amp;tt=1590919237477.12.12.13" name="iframeu6056789_0" width="300" height="250" frameborder="0" marginwidth="0" marginheight="0" scrolling="no" align="center,center"></iframe></div>
</div>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">5月30日，一名示威者在纽约声援弗洛伊德的集会活动中被警方逮捕。图源：美联社</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">因非裔男子乔治&middot;弗洛伊德被白人警察德雷克&middot;乔文&ldquo;膝盖锁喉&rdquo;致死，美国明尼苏达州的抗议活动持续进行，30日已是第5天。此外，这股抗议浪潮正向全美延烧，纽约、洛杉矶、休斯顿等至少30座美国城市也爆发大规模示威，场面混乱如同&ldquo;战场&rdquo;。</p>', null, null, 1, null, null, 0, null, null, 2, null, null, null, '1', 'admin', '2020-05-31 10:00:45', null, null, null);
INSERT INTO cms.t_article (id, article_title, sub_title, type_id, type_name, simple_intro, content, title_color, bold_flag, review_flag, top_flag, ad_flag, delete_flag, recommend_flag, keyword, status, delete_no, delete_name, delete_time, add_no, add_name, add_time, update_no, update_name, update_time) VALUES (25, '发骚扰短信也算侵犯隐私权 民法典明确禁止这些行为', '（原标题：响一声就完了？发骚扰短信也算侵犯隐私权 民法典明确禁止这些侵权行为）', 6, null, '', '<p class="otitle" style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">&nbsp;</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">&nbsp;</p>
<div class="video-wrapper" style="margin: 10px auto; color: #404040; font-family: ''Microsoft Yahei''; font-size: 18px; text-align: justify; background: #f5f5f5;">
<div class="video-inner" style="padding: 25px 25px 20px;">
<div class="video" style="background: #000000; max-height: 450px;"><video style="height: 343.125px; max-height: 450px;" poster="https://videoimg.nos-jd.163yun.com/cmssnapshot/20200531/vFQg95177_8.jpg" preload="auto" controls="controls" width="100%"></video></div>
<div class="video-info clearfix" style="margin-top: 16px; height: 20px; line-height: 20px;">
<p class="fn-right" style="font-size: 12px; height: 22px; line-height: 22px; overflow: hidden; float: right;"><a class="video-title" style="color: #000000; text-align: right; float: left; width: 300px;" href="https://v.163.com/static/3/VKD16K9UD.html" target="_blank" rel="noopener">响一声就完了？发骚扰短信也算侵犯隐私权 民法典明确禁止这些侵权行为</a><span class="video-from" style="color: #a7a7a7; margin-left: 10px;">（来源：视频综合）</span></p>
</div>
<div class="video-list">&nbsp;</div>
</div>
</div>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">&nbsp;</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">1986年，民法通则规定了姓名权、荣誉权、名誉权、生命权、身体健康权、自由权，肖像权，统称为人身权。从此，越来越多的人开始有了保护这些权利的意识。这短短的规定，奠定了中国人格权法律发展完善的基础。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">34年后，民法典在民法通则的基础上，改人身权为人格权，以独立篇章的形式确立其在民法典中的地位，强调保护公民的生命健康、姓名、肖像、名誉、荣誉等重要权利，还增加了隐私权，以及个人信息保护。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">此前，民法没有把隐私权确立为一项独立的人格权，随着人们保护隐私的意识和需求越来越强，因而在司法实践中，一般会借助司法解释并通过保护名誉权或以维护公序良俗等方式对公民的隐私权益进行保护。但信息社会衍生出来的司法难题越来越多，这种间接的保护方法已不足以应对。</p>
<p class="f_center" style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; color: #404040; text-align: center; background-color: #ffffff;"><img style="vertical-align: top; border: 0px; max-width: 550px; margin-right: auto; margin-left: auto;" src="https://cms-bucket.ws.126.net/2020/0531/c73019edj00qb6eah000ic000e80080c.jpg" alt="发骚扰短信也算侵犯隐私权 民法典明确禁止这些行为" /></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;"><span style="font-weight: bold;">中国人民大学常务副校长、中国法学会民法学研究会会长&nbsp;王利明：</span></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">我到过北京互联网法院去调研，他们已经受理了大概大几百件涉及到人格权侵权的案件，其中不少就是关于私生活安宁的案件，比如偷拍、偷录、偷窥、性骚扰，非法公布不如的位置信息等等，这些都是过去我们没有遇到过的，但是在今天互联网高科技时代，这又是我们摆在我们面前的确实必须要应面对的非常严重的社会问题。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;"><span style="font-weight: bold;">技术改变生活</span></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;"><span style="font-weight: bold;">个人隐私的内涵快速变化</span></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;"><span style="font-weight: bold;">法律保护的范围难以覆盖</span></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;"><span style="font-weight: bold;">隐私权和个人信息保护越来越成为难点与痛点</span></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;"><span style="font-weight: bold;">私人生活空间免于被侵犯成为公众的期盼</span></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;"><span style="font-weight: bold;">这个时候法律怎么规定才能做到有效？</span></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;"><span style="font-weight: bold;">王利明：</span></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">首先要把每个人究竟享有哪些隐私的权利，哪些权利可以受到保护，然后对那些侵害这些权利的行为要作出禁止性的规定，要把它列举出来。哪些侵害了个人的私生活安宁的隐私权，然后再具体规定各种保护的方法。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;"><span style="font-weight: bold;">民法典规定：</span></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">自然人享有隐私权，任何组织或者个人不得以刺探、侵扰、泄露、公开等方式侵害他人的隐私权。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;"><span style="font-weight: bold;">民法典对隐私作了清晰定义：</span></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">隐私是自然人的私人生活安宁和不愿为他人知晓的私密空间、私密活动、私密信息。</p>
<p class="f_center" style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; color: #404040; text-align: center; background-color: #ffffff;"><img style="vertical-align: top; border: 0px; max-width: 550px; margin-right: auto; margin-left: auto;" src="https://cms-bucket.ws.126.net/2020/0531/c544573fj00qb6eah0011c000hs00a0c.jpg" alt="发骚扰短信也算侵犯隐私权 民法典明确禁止这些行为" /></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;"><span style="font-weight: bold;">中国人民大学教授&nbsp;杨立新：&nbsp;</span></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">所谓的隐私权是什么？我的私生活别人不可以干预，别人不可以侵犯，别人不可以给公开。我们隐私当中包含最重要的内容是私人生活安宁，就把隐私权最核心的部分概括起来了。我们现在每天有大量的骚扰邮件、信件、电邮，短信、垃圾短信。有人说就发一个短信，你不就听着噔一声就完了，有什么损害？其实有损害，这侵害了我的生活安宁，你噔他噔每一天都给我噔，我就不烦躁吗？按照现在这都是侵扰我的生活安宁，都是侵权行为。</p>
<p class="f_center" style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; color: #404040; text-align: center; background-color: #ffffff;"><img style="vertical-align: top; border: 0px; max-width: 550px; margin-right: auto; margin-left: auto;" src="https://cms-bucket.ws.126.net/2020/0531/12645365j00qb6eah000zc000hs00a0c.jpg" alt="发骚扰短信也算侵犯隐私权 民法典明确禁止这些行为" /></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">民法典中，还列举了明确禁止实施的一些侵权行为，比如骚扰电话、骚扰短信、骚扰邮件、传单；进入他人的房间窥视、偷拍；跟踪、偷听等都是侵犯隐私权的形式。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">这些严谨平实的法条将成为每一个个体私密生活安宁的保障。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;"><span style="font-weight: bold;">守护个人安宁 实现对人的尊重</span></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">民法典第111条曾被认为与一位18岁女孩的遭遇有关，事情缘于2016年8月，山东刚接到大学录取通知书的徐玉玉，被诈骗电话骗走了上大学的费用9900元，在焦虑压抑的情况下她突发心源性休克，不幸离世。徐玉玉之死，被归结为电信诈骗团伙精准获取受害人信息导致的惨剧。徐玉玉案使大数据时代个人信息保护问题再度被热议。</p>
<p class="f_center" style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; color: #404040; text-align: center; background-color: #ffffff;"><img style="vertical-align: top; border: 0px; max-width: 550px; margin-right: auto; margin-left: auto;" src="https://cms-bucket.ws.126.net/2020/0531/47b0bfbdj00qb6eah0010c000hs00a0c.jpg" alt="发骚扰短信也算侵犯隐私权 民法典明确禁止这些行为" /></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">徐玉玉去世一年后，2017年通过审议的民法总则规定：&ldquo;自然人的个人信息受法律保护。任何组织或者个人需要获取他人个人信息的,应当依法取得并确保信息安全,不得非法收集、使用、加工、传输他人个人信息,不得非法 买卖、提供或者公开他人个人信息。&rdquo;</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">一个普通公民的遭遇引发社会的反思、制度的补救、立法的回应，甚至被民众以&ldquo;徐玉玉条款&rdquo;来别称法条，折射出了国家法典对公民权利和生命的尊重。</p>
<p class="f_center" style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; color: #404040; text-align: center; background-color: #ffffff;"><img style="vertical-align: top; border: 0px; max-width: 550px; margin-right: auto; margin-left: auto;" src="https://cms-bucket.ws.126.net/2020/0531/26e0893cj00qb6eah000fc000e80080c.jpg" alt="发骚扰短信也算侵犯隐私权 民法典明确禁止这些行为" /></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;"><span style="font-weight: bold;">全国人大代表、山东省高级人民法院院长 张甲天：</span></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">这一次民法典，人格权编，对隐私和个人信息设立专章与保护，非常好。我相信民法典实施以后，必将极大的促进互联网经济的健康顺利发展。同时也为人民法院依法办理这类案件提供了准绳和遵循。</p>', null, null, 1, null, null, 0, null, null, 3, null, null, null, '1', 'admin', '2020-05-31 10:01:15', null, null, null);
INSERT INTO cms.t_article (id, article_title, sub_title, type_id, type_name, simple_intro, content, title_color, bold_flag, review_flag, top_flag, ad_flag, delete_flag, recommend_flag, keyword, status, delete_no, delete_name, delete_time, add_no, add_name, add_time, update_no, update_name, update_time) VALUES (26, '2019中国中学生烟草调查：初中生吸电子烟比例上升', '（原标题：2019年中国中学生烟草调查结果发布）', 6, null, '', '<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">今天（5月31日）是第33个世界无烟日，主题是&ldquo;保护青少年远离传统烟草产品和电子烟&rdquo;。中国疾病预防控制中心发布了2019年中国中学生烟草调查结果。</p>
<p class="f_center" style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; color: #404040; text-align: center; background-color: #ffffff;"><img style="vertical-align: top; border: 0px; max-width: 550px; margin-right: auto; margin-left: auto;" src="https://cms-bucket.ws.126.net/2020/0531/267536eap00qb6de400b7c000fs00atc.png" /></p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">全国31个省（自治区、直辖市）的347个县（市、区），共1024所初中和974所高中（含277所职业学校）的288192名学生接受了调查。结果显示，初中学生卷烟使用率明显下降，电子烟使用率显著上升，职业学校控烟工作值得关注。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">初中生吸卷烟比例明显下降 使用电子烟比例显著上升</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">结果显示，过去5年我国初中学生尝试吸卷烟和现在吸卷烟的比例明显下降。2019年初中学生尝试吸卷烟的比例为12.9%，现在吸卷烟的比例为3.9%，与2014年相比，分别下降了5%和2%。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">值得注意的是，过去5年我国初中学生听说过电子烟和现在使用电子烟的比例显著上升。2019年初中学生听说过电子烟的比例为69.9%，现在电子烟使用率为2.7%，与2014年相比，分别上升了24.9%和1.5%。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">高中生吸烟情率远高于初中学生</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">今年我国首次将全国高中学生吸烟情况纳入中学生烟草调查，结果发现高中学生吸烟率远高于初中学生，职业学校控烟情况更是不容乐观。2019年高中学生尝试吸卷烟、现在吸卷烟以及现在使用电子烟的比例分别为24.5%、8.6%和3.0%，均高于初中学生。其中，职业学校学生高于普通高中学生，分别为30.3%、14.7%和4.5%，职业学校男生高达43.2%、23.3%和7.1%。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">中学生二手烟暴露情况有所改善 但仍较为严重</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">与2014年相比，2019年初中学生过去7天内，在家、室内公共场所、室外公共场所或公共交通工具看到有人吸烟的比例均有明显下降，总体比例下降了9.7个百分点，但仍高达63.2%；在过去30天看到有人在校园内吸烟的比例和在学校内几乎每天看到教师吸烟的比例分别为45.2%和8.5%，与2014年比均有所下降。普通高中和职业学校学生在上述四类场所看到有人吸烟的比例均高于初中学生，分别为72.0%和67.3%；在过去30天看到有人在校园内吸烟的比例分别为57.3%和58.6%，在学校内几乎每天看到教师吸烟的比例分别为13.0%和9.4%。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">中学生对吸烟成瘾的认知水平亟待进一步提升</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">初中学生过去12个月内在课堂上学习过烟草使用导致的具体健康危害后果的比例为58.6%，与2014年比有所上升。普通高中和职业学校学生过去12个月内在课堂上学习过烟草使用导致的具体健康危害后果的比例分别为51.3%和60.0%。我国初中、普通高中和职业学校学生认为开始吸烟后很难戒断的比例仅为35.0%、25.6%和26.2%。</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">影响中学生吸烟的因素仍广泛存在</p>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">&nbsp;</p>
<div class="gg200x300" style="padding: 19px; margin-right: 40px; overflow: hidden; text-align: center; font-size: 0px; line-height: 0; float: left; border: 1px solid #e5e5e5; position: relative; color: #404040; font-family: ''Microsoft Yahei''; background-color: #ffffff;">
<div id="ssp_6056789"><iframe id="iframeu6056789_0" style="display: block; border-width: 0px; border-style: initial; vertical-align: bottom; width: 300px; height: 250px;" src="https://pos.baidu.com/vcam?conwid=300&amp;conhei=250&amp;rdid=6056789&amp;dc=3&amp;exps=110011&amp;psi=62383bd7abfbebdbbefe417163fc28f2&amp;di=u6056789&amp;dri=0&amp;dis=0&amp;dai=2&amp;ps=2393x551&amp;enu=encoding&amp;ant=0&amp;dcb=___adblockplus_&amp;dtm=HTML_POST&amp;dvi=0.0&amp;dci=-1&amp;dpt=none&amp;tsr=0&amp;tpr=1590919287809&amp;ti=2019%E4%B8%AD%E5%9B%BD%E4%B8%AD%E5%AD%A6%E7%94%9F%E7%83%9F%E8%8D%89%E8%B0%83%E6%9F%A5%EF%BC%9A%E5%88%9D%E4%B8%AD%E7%94%9F%E5%90%B8%E7%94%B5%E5%AD%90%E7%83%9F%E6%AF%94%E4%BE%8B%E4%B8%8A%E5%8D%87_%E7%BD%91%E6%98%93%E6%96%B0%E9%97%BB&amp;ari=2&amp;ver=0527&amp;dbv=2&amp;drs=1&amp;pcs=1903x979&amp;pss=1903x4452&amp;cfv=0&amp;cpl=3&amp;chi=1&amp;cce=true&amp;cec=GBK&amp;tlm=1590919267&amp;prot=2&amp;rw=979&amp;ltu=https%3A%2F%2Fnews.163.com%2F20%2F0531%2F10%2FFDUUS66O0001899O.html&amp;ltr=https%3A%2F%2Fnews.163.com%2F&amp;lcr=https%3A%2F%2Fwww.google.com.hk%2F&amp;ecd=1&amp;uc=1920x1050&amp;pis=-1x-1&amp;sr=1920x1080&amp;tcn=1590919288&amp;qn=b520aa738663218a&amp;tt=1590919287800.16.16.17" name="iframeu6056789_0" width="300" height="250" frameborder="0" marginwidth="0" marginheight="0" scrolling="no" align="center,center"></iframe></div>
</div>
<p style="margin-top: 32px; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 18px; line-height: 32px; font-family: ''Microsoft Yahei''; text-indent: 2em; color: #404040; text-align: justify; background-color: #ffffff;">本次调查还发现，影响中学生吸烟的因素仍广泛存在。一是不向未成年人售烟的法律仍未得到有效落实。最近一次买烟时没有因为年龄被拒绝的比例，初中、普通高中和职业学校学生分别高达76.5%，87.6%和87.6%。二是卷烟变得越来越&ldquo;便宜&rdquo;。初中、普通高中和职业学校学生最近一次购买20支卷烟花费在10元以上的比例分别为70.6%、85.3%和77.0%，平均价格高于成人。同时，7.3%的初中学生、2.8%的普通高中学生和2.3%的职业学校学生报告自己买的卷烟价格在5元及以下，有的甚至不足3元。三是烟草广告、促销和赞助活动仍然广泛存在。过去30天内去过烟草零售点的初中、普通高中和职业学校学生在烟草零售点看到过烟草产品广告或促销的比例分别为48.9%、42.0%和46.7%；过去30天内使用过互联网的初中、普通高中和职业学校学生在互联网上看到过烟草产品广告或视频的比例分别为23.2%、21.4%和27.7%；得到过烟草公司工作人员提供免费烟草产品的比例分别为2.0%、2.1%和2.8%；同时，16.2%的初中吸烟学生、8.8%的普通高中吸烟学生和3.7%的职业学校吸烟学生报告自己最近一次买烟是按&ldquo;支&rdquo;购买的。四是影视剧中的吸烟镜头尚未得到有效控制。过去30天内看过电视、录像/视频或者电影的初中、普通高中和职业学校学生报告在电视、录像/视频或者电影中看到有人吸烟的比例分别为69.5%、72.9%和77.4%。（总台央视记者 郑怡哲）</p>', null, null, 1, null, null, 0, null, null, 2, null, null, null, '1', 'admin', '2020-05-31 10:01:40', null, null, null);